INSERT INTO `sections` (`articleId`,`type`,`pos`,`align`,`content`) VALUES (81,'text',6,'left','{\"text\":\"<em>Till sist. Den Kommunala Musikskolan, numera Kulturskolan och musikverksamheten i Svenska Kyrkan, fr√§mst k√∂rer i olika former √§r det som tillsamman med det fria musiklivet i Kalix format basen f√∂r mig som professionell s√•ngare och musiker. Jag ser med tacksamhet tillbaka p√• alla de som gjort skillnad och bidragit till att jag kunnat tillbringa ett yrkesliv p√• scenen.</em>\"}');
